package com.EsportLeagueStats.ESLS.service;import com.EsportLeagueStats.ESLS.model.*;import com.EsportLeagueStats.ESLS.persistance.FinaleRepository;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class FinaleService {    private final FinaleRepository repository;    private final UtakmicaService utakmicaService;    private final SezonaService sezonaService;    private final TimService timService;    public FinaleService(FinaleRepository repository, UtakmicaService utakmicaService, SezonaService sezonaService, TimService timService) {        this.repository = repository;        this.utakmicaService = utakmicaService;        this.sezonaService = sezonaService;        this.timService = timService;    }    public List<Finale> findAllFinals() {        return repository.findAll();    }    public Finale findFinaleById(FinalePK id) {        return repository.findById(id).                orElseThrow(() -> new RuntimeException("Finaleto ne e vo bazata"));    }    public void deleteFinale(FinalePK id) {        Finale finale = new Finale(id);        repository.delete(finale);    }    public Finale addFinale(int uid, String finaleMestoNaOdrzuvanje, int seid, int tid) {        Utakmica utakmica = utakmicaService.findUtakmicaById(uid);        Sezona sezona = sezonaService.findSezonaById(seid);        Tim tim = timService.findTimById(tid);        Finale finale = new Finale(finaleMestoNaOdrzuvanje, sezona, tim, utakmica);        return repository.save(finale);    }}