package com.EsportLeagueStats.ESLS.service;import com.EsportLeagueStats.ESLS.model.Liga;import com.EsportLeagueStats.ESLS.persistance.LigaRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class LigaService  {    private final LigaRepository ligaRepository;    @Autowired    public LigaService(LigaRepository ligaRepository) {        this.ligaRepository = ligaRepository;    }    public List<Liga> findAllLiga() {        return ligaRepository.findAll();    }    public Liga findLigaById(int id) {        return ligaRepository.findById(id)                .orElseThrow(()-> new RuntimeException("Nema takva Liga"));    }    public Liga addLiga(String lKontinent, String lIme, String mestoNaOdrzuvanje) {        Liga liga = new Liga(lKontinent,lIme,mestoNaOdrzuvanje);        return ligaRepository.save(liga);    }}