package com.EsportLeagueStats.ESLS.service;import com.EsportLeagueStats.ESLS.model.Menuvaattim;import com.EsportLeagueStats.ESLS.model.MenuvaattimPK;import com.EsportLeagueStats.ESLS.persistance.MenuvaatTimRepository;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class MenuvaatTimService {    private final MenuvaatTimRepository repository;    public MenuvaatTimService(MenuvaatTimRepository repository) {        this.repository = repository;    }    public List<Menuvaattim> findAllTransfers(){        return repository.findAll();    }    public Menuvaattim findTransferById(MenuvaattimPK id){        return repository.findById(id).orElseThrow(()-> new RuntimeException("Exception"));    }}