package com.EsportLeagueStats.ESLS.service;import com.EsportLeagueStats.ESLS.model.Players;import com.EsportLeagueStats.ESLS.model.Sezona;import com.EsportLeagueStats.ESLS.model.StatistikaNaIgrach;import com.EsportLeagueStats.ESLS.persistance.StatistikaNaIgrachRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class StatistikaNaIgrachService {    private final StatistikaNaIgrachRepository repository;    @Autowired    public StatistikaNaIgrachService(StatistikaNaIgrachRepository repository) {        this.repository = repository;    }    public List<StatistikaNaIgrach> findAllPlayerStats() {        return repository.findAll();    }    public StatistikaNaIgrach findStatsIgrachById(int id) {        return repository.findById(id).orElseThrow(() -> new RuntimeException("Nema Takov sTat"));    }    public StatistikaNaIgrach addStatistika(Integer kda, Integer nadigruvanja, Integer napravenaSteta,                                            Integer poeniVo10Min, Integer primenaSteta, Integer prinudeniGreski,                                            Integer prisustvoNaSoigrachi, Integer prisustvoNaVardovi, Players igrach,                                            Sezona sezona) {        StatistikaNaIgrach statistikaNaIgrach = new StatistikaNaIgrach(kda,                nadigruvanja, napravenaSteta, poeniVo10Min,                primenaSteta, prinudeniGreski, prisustvoNaSoigrachi,                prisustvoNaVardovi, igrach, sezona);        return repository.save(statistikaNaIgrach);    }}