package com.EsportLeagueStats.ESLS.service;import com.EsportLeagueStats.ESLS.model.Igrachi;import com.EsportLeagueStats.ESLS.model.StatistikaNaIgrach;import com.EsportLeagueStats.ESLS.persistance.StatistikaNaIgrachRepository;import org.hibernate.stat.internal.StatsHelper;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class StatistikaNaIgrachService {    private final StatistikaNaIgrachRepository repository;    private final IgrachService igrachService;    @Autowired    public StatistikaNaIgrachService(StatistikaNaIgrachRepository repository, IgrachService igrachService) {        this.repository = repository;        this.igrachService = igrachService;    }    public List<StatistikaNaIgrach> findAllPlayerStats(){        return repository.findAll();    }    public StatistikaNaIgrach findStatsIgrachById(int id){        return repository.findById(id).orElseThrow(()-> new RuntimeException("Nema Takov sTat"));    }    public void addStatistikaNaIgrach(int iid,int kda, int nadigruvanja, int napravena_steta, int poeni_vo_10_min, int primena_steta, int prinudeni_greski, int prisustvo_na_soigrachi, int prisustvo_na_vardovi)    {        Igrachi igrach = igrachService.findIgrachById(iid);        StatistikaNaIgrach statistikaNaIgrach = new StatistikaNaIgrach(kda, nadigruvanja, napravena_steta, poeni_vo_10_min, primena_steta, prinudeni_greski, prisustvo_na_soigrachi,  prisustvo_na_vardovi);        repository.save(statistikaNaIgrach);    }}