package com.EsportLeagueStats.ESLS.service;import com.EsportLeagueStats.ESLS.model.StatistikaNaIgrach;import com.EsportLeagueStats.ESLS.model.StatistikaNaTim;import com.EsportLeagueStats.ESLS.persistance.StatistikaNaIgrachRepository;import com.EsportLeagueStats.ESLS.persistance.StatistikaNaTimRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class StatistikaNaTimService {    private final StatistikaNaTimRepository repository;    public StatistikaNaTimService(StatistikaNaTimRepository repository) {        this.repository = repository;    }    public List<StatistikaNaTim> findAllTimStats(){        return repository.findAll();    }    public StatistikaNaTim findStatsTimById(int id){        return repository.findById(id).orElseThrow(()-> new RuntimeException("Nema Takov stat"));    }}