package com.EsportLeagueStats.ESLS.service;import com.EsportLeagueStats.ESLS.model.Liga;import com.EsportLeagueStats.ESLS.model.Tim;import com.EsportLeagueStats.ESLS.persistance.TimRepository;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.util.List;@Servicepublic class TimService  {    private final TimRepository repository;    private final LigaService ligaService;    public TimService(TimRepository timRepository, LigaService ligaService) {        this.repository = timRepository;        this.ligaService = ligaService;    }    public List<Tim> findAllTimovi() {        return repository.findAll();    }    public Tim findTimById(int id) {      return  repository.findById(id)              .orElseThrow(() -> new RuntimeException("Timot ne e pronajden vo bazata"));    }    @Transactional    public Tim addTim(String ime, String logo, int lid) {       Liga liga = ligaService.findLigaById(lid);       Tim tim = new Tim(ime,logo,liga);       return repository.save(tim);    }}