package com.EsportLeagueStats.ESLS.service;import com.EsportLeagueStats.ESLS.model.Sezona;import com.EsportLeagueStats.ESLS.model.Utakmica;import com.EsportLeagueStats.ESLS.persistance.UtakmicaRepository;import org.springframework.stereotype.Service;import java.util.List;import java.util.Optional;@Servicepublic class UtakmicaService {    private final UtakmicaRepository repository;    private final SezonaService  service;    public UtakmicaService(UtakmicaRepository repository, SezonaService service) {        this.repository = repository;        this.service = service;    }    public List<Utakmica> findAllUtakmicas(){        return  repository.findAll();    }    public Utakmica findUtakmicaById(int id){        return  repository.findById(id).orElseThrow(()-> new RuntimeException("utakmica not found"));    }    public Utakmica addUtakmica(String pobednik, String vremeTraenje, int seid){        Sezona sezona = service.findSezonaById(seid);        Utakmica utakmica = new Utakmica(pobednik,vremeTraenje,sezona);        return repository.save(utakmica);    }    public void deleteUtakmica(int id){        Utakmica utakmica = findUtakmicaById(id);        repository.delete(utakmica);    }}