package com.EsportLeagueStats.ESLS.service.implementation;import com.EsportLeagueStats.ESLS.model.Liga;import com.EsportLeagueStats.ESLS.persistance.LigaRepository;import com.EsportLeagueStats.ESLS.service.LigaServiceInterface;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Optional;@Servicepublic class LigaService implements LigaServiceInterface {    private final LigaRepository ligaRepository;    @Autowired    public LigaService(LigaRepository ligaRepository) {        this.ligaRepository = ligaRepository;    }    @Override    public List<Liga> findAllLiga() {        return ligaRepository.findAll();    }    @Override    public Liga findLigaById(int id) {        return ligaRepository.findById(id)                .orElseThrow(()-> new RuntimeException("Nema takva Liga"));    }    @Override    public Liga addLiga(String lKontinent, String lIme, String mestoNaOdrzuvanje) {        Liga liga = new Liga(lKontinent,lIme,mestoNaOdrzuvanje);        return ligaRepository.save(liga);    }}