package com.EsportLeagueStats.ESLS.service.implementation;import com.EsportLeagueStats.ESLS.model.Sezona;import com.EsportLeagueStats.ESLS.persistance.SezonaRepository;import com.EsportLeagueStats.ESLS.service.SezonaServiceInterface;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class SezonaService  implements SezonaServiceInterface {    private final SezonaRepository repository;    public SezonaService(SezonaRepository sezonaRepository) {        this.repository = sezonaRepository;    }    @Override    public List<Sezona> findAllSezona() {        return repository.findAll();    }    @Override    public Sezona findSezonaById(int id) {        return repository.findById(id).orElseThrow(() -> new RuntimeException("Nema takva sezona"));    }    @Override    public Sezona addSezona(String kraenDatum, String pocetenDatum) {        Sezona sezona = new Sezona(kraenDatum,pocetenDatum);        return repository.save(sezona);    }}