package com.EsportLeagueStats.ESLS.web;import com.EsportLeagueStats.ESLS.model.Finale;import com.EsportLeagueStats.ESLS.model.FinalePK;import com.EsportLeagueStats.ESLS.service.FinaleService;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/finals")public class FinaleController {    private final FinaleService service;    public FinaleController(FinaleService service) {        this.service = service;    }    @RequestMapping("/all")    public List<Finale> findAllFinals() {        return service.findAllFinals();    }    @GetMapping("/{id}")    public Finale findFinaleById(@PathVariable("id") FinalePK id) {        return service.findFinaleById(id);    }}