package com.EsportLeagueStats.ESLS.web;import com.EsportLeagueStats.ESLS.model.Finale;import com.EsportLeagueStats.ESLS.model.FinalePK;import com.EsportLeagueStats.ESLS.service.FinaleService;import com.EsportLeagueStats.ESLS.web.requests.AddFinaleRequest;import org.springframework.http.HttpStatus;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/finals")public class FinaleController {    private final FinaleService service;    public FinaleController(FinaleService service) {        this.service = service;    }    @RequestMapping("/all")    public List<Finale> findAllFinals() {        return service.findAllFinals();    }    @GetMapping("/{id}")    public Finale findFinaleById(@PathVariable("id") FinalePK id) {        return service.findFinaleById(id);    }    @PostMapping    public Finale addFinale(@RequestBody AddFinaleRequest request){        return service.addFinale(request.getUid(),request.getFinaleMestoNaOdrzuvanje(),request.getSeid(),request.getTid());    }    @DeleteMapping("/{id}")    @ResponseStatus(HttpStatus.NO_CONTENT)    public void deleteFinale(@PathVariable("id") FinalePK id){ service.deleteFinale(id);}}