package com.EsportLeagueStats.ESLS.web;import com.EsportLeagueStats.ESLS.model.FinalePK;import com.EsportLeagueStats.ESLS.model.Igraat;import com.EsportLeagueStats.ESLS.model.IgraatPK;import com.EsportLeagueStats.ESLS.service.IgraatService;import org.springframework.http.HttpStatus;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/igraat")public class IgraatController {    private final IgraatService service;    public IgraatController(IgraatService service) {        this.service = service;    }    @GetMapping("/all")    public List<Igraat>  findAllIgrani(){        return service.findAllIgrani();    }    @GetMapping("/{id}")    public Igraat findIgranaById(IgraatPK id){ return  service.findIgraatById(id);}    @DeleteMapping("/{id}")    @ResponseStatus(HttpStatus.NO_CONTENT)    public void deleteIgrana(IgraatPK id) {  service.deleteIgrana(id);}}