package com.EsportLeagueStats.ESLS.web;import com.EsportLeagueStats.ESLS.model.Igraat;import com.EsportLeagueStats.ESLS.service.IgraatService;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.List;@RestController@RequestMapping("/igraat")public class IgraatController {    private final IgraatService service;    public IgraatController(IgraatService service) {        this.service = service;    }    @GetMapping("/all")    public List<Igraat>  findAllIgrani(){        return service.findAllIgrani();    }}