package com.EsportLeagueStats.ESLS.web;import com.EsportLeagueStats.ESLS.model.Liga;import com.EsportLeagueStats.ESLS.service.LigaService;import com.EsportLeagueStats.ESLS.web.requests.AddLigaRequest;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/api/liga")public class LigaController {    private final LigaService service;    public LigaController(LigaService service) {        this.service = service;    }    @GetMapping    public List<Liga> findAllLiga(){        return service.findAllLiga();    }    @GetMapping("/{id}")    public Liga findLigaById( @PathVariable("id")  int id){        return service.findLigaById(id);    }    @PostMapping    public Liga addLiga(@RequestBody AddLigaRequest request){        return service.addLiga(request.getKontinent(),request.getIme(),request.getMestoNaOdrzuvanje());    }}