package com.EsportLeagueStats.ESLS.web;import com.EsportLeagueStats.ESLS.model.Liga;import com.EsportLeagueStats.ESLS.service.implementation.LigaService;import com.EsportLeagueStats.ESLS.web.requests.AddLigaRequest;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping(value = "/liga")public class LigaController {    private final LigaService service;    public LigaController(LigaService service) {        this.service = service;    }    @GetMapping("/all")    public List<Liga> findAllLiga(){        return service.findAllLiga();    }    @GetMapping("/{id}")    public Liga findLigaById( @PathVariable("id")  int id){        return service.findLigaById(id);    }    @PostMapping    public Liga addLiga(@RequestBody AddLigaRequest request){        return service.addLiga(request.getlKontinent(),request.getlIme(),request.getMestoNaOdrzuvanje());    }}