package com.EsportLeagueStats.ESLS.web;import com.EsportLeagueStats.ESLS.model.Menuvaattim;import com.EsportLeagueStats.ESLS.service.MenuvaatTimService;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.List;@RestController@RequestMapping("/transfers")public class MenuvaatTimController {    private final MenuvaatTimService service;    public MenuvaatTimController(MenuvaatTimService service) {        this.service = service;    }    @GetMapping("/all")    public List<Menuvaattim> findAllTransfers(){        return service.findAllTransfers();    }}