package com.EsportLeagueStats.ESLS.web;import com.EsportLeagueStats.ESLS.model.Sezona;import com.EsportLeagueStats.ESLS.service.SezonaService;import com.EsportLeagueStats.ESLS.web.requests.AddSezonaRequest;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/api/sezona")public class SezonaController {    private final SezonaService service;    public SezonaController(SezonaService service) {        this.service = service;    }    @GetMapping    public List<Sezona> findAllSeasons(){        return service.findAllSezona();    }    @GetMapping("/{id}")    public Sezona findSezonaById(@PathVariable("id") int id){        return service.findSezonaById(id);    }    @PostMapping    public Sezona addSezona(@RequestBody AddSezonaRequest request){        return service.addSezona(request.getYear(), request.getKraenDatum(),request.getKraenDatum());}}