package com.EsportLeagueStats.ESLS.web;import com.EsportLeagueStats.ESLS.model.StatistikaNaIgrach;import com.EsportLeagueStats.ESLS.service.StatistikaNaIgrachService;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.List;@RestController@RequestMapping("/statsPlayer")public class StatistikaNaIgrachController {    private final StatistikaNaIgrachService service;    public StatistikaNaIgrachController(StatistikaNaIgrachService service) {        this.service = service;    }    @GetMapping("/all")    public List<StatistikaNaIgrach> findAllStatsPlayers() {        return service.findAllPlayerStats();    }    @GetMapping("/{id}")    public StatistikaNaIgrach findIgrachById(@PathVariable("id") int id){        return service.findStatsIgrachById(id);    }}