package com.EsportLeagueStats.ESLS.web;import com.EsportLeagueStats.ESLS.model.StatistikaNaTim;import com.EsportLeagueStats.ESLS.service.StatistikaNaTimService;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.List;@RestController@RequestMapping("/statsTim")public class StatistikaNaTimController {    private final StatistikaNaTimService service;    public StatistikaNaTimController(StatistikaNaTimService service) {        this.service = service;    }    @GetMapping("/all")    public List<StatistikaNaTim> findallTimStats(){        return service.findAllTimStats();    }    @GetMapping("/{id}")    public StatistikaNaTim findStatistikaNaTimById(@PathVariable("id") int id){        return service.findStatsTimById(id);    }}