package com.EsportLeagueStats.ESLS.web;import com.EsportLeagueStats.ESLS.model.Tim;import com.EsportLeagueStats.ESLS.service.TimService;import com.EsportLeagueStats.ESLS.web.requests.AddTimRequest;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/timovi")public class TimController {    private final TimService service;    public TimController(TimService service) {        this.service = service;    }    @GetMapping("/all")    public List<Tim> findAllTimovi() {        return service.findAllTimovi();    }    @GetMapping("/{id}")    public Tim findTimById(@PathVariable("id") int id) {        return service.findTimById(id);    }    @PostMapping    public Tim addTim(@RequestBody AddTimRequest request) {        return service.addTim(request.getIme(), request.getLogo(), request.getLigaId());    }}