package com.EsportLeagueStats.ESLS.web;import com.EsportLeagueStats.ESLS.model.Utakmica;import com.EsportLeagueStats.ESLS.service.UtakmicaService;import org.springframework.http.HttpStatus;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/utakmica")public class UtakmicaController {    private final UtakmicaService service;    public UtakmicaController(UtakmicaService service) {        this.service = service;    }    @RequestMapping("/all")    public List<Utakmica> findAllUtakmicas(){        return   service.findAllUtakmicas();    }    @GetMapping("/{id}")    public Utakmica findUtakmicaById(@PathVariable("id") int id){        return service.findUtakmicaById(id);    }    @DeleteMapping("/{id}")    @ResponseStatus(HttpStatus.NO_CONTENT)    public void deleteIgrach(@PathVariable("id") int id){        service.deleteUtakmica(id);    }}