package com.EsportLeagueStats.ESLS.web;import com.EsportLeagueStats.ESLS.model.Utakmica;import com.EsportLeagueStats.ESLS.service.UtakmicaService;import com.EsportLeagueStats.ESLS.web.requests.AddUtakmkicaRequest;import org.springframework.http.HttpStatus;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/api/utakmica")public class UtakmicaController {    private final UtakmicaService service;    public UtakmicaController(UtakmicaService service) {        this.service = service;    }    @GetMapping    public List<Utakmica> findAllUtakmicas(){        return   service.findAllUtakmicas();    }    @GetMapping("/{id}")    public Utakmica findUtakmicaById(@PathVariable("id") int id){        return service.findUtakmicaById(id);    }    @PostMapping    public Utakmica createUtakmica(@RequestBody AddUtakmkicaRequest request) {        if(request.getGubitnik() == request.getPobednik()) {            throw new RuntimeException("Ne mozhe tim da igra megju sebe");        }        return service.addUtakmica(request.getPobednik(), request.getGubitnik(), request.getYear(), request.getLiga(), request.isFinale());    }    @DeleteMapping("/{id}")    @ResponseStatus(HttpStatus.NO_CONTENT)    public void deleteUtakmica(@PathVariable("id") int id){        service.deleteUtakmica(id);    }}