package com.EsportLeagueStats.ESLS.web.requests;import java.io.Serializable;public class AddIgrachRequest  implements Serializable {    private String akronim;    private String drzava;    private String ime;    private String pozicija;    public AddIgrachRequest(){}    public AddIgrachRequest(String akronim, String drzava, String ime, String pozicija) {        this.akronim = akronim;        this.drzava = drzava;        this.ime = ime;        this.pozicija = pozicija;    }    public String getAkronim() {        return akronim;    }    public String getDrzava() {        return drzava;    }    public String getIme() {        return ime;    }    public String getPozicija() {        return pozicija;    }}