package com.EsportLeagueStats.ESLS.web.requests;public class AddLigaRequest {    private String lKontinent;    private String lIme;    private String mestoNaOdrzuvanje;    public AddLigaRequest(){}    public AddLigaRequest(String lKontinent, String lIme, String mestoNaOdrzuvanje) {        this.lKontinent = lKontinent;        this.lIme = lIme;        this.mestoNaOdrzuvanje = mestoNaOdrzuvanje;    }    public String getlKontinent() {        return lKontinent;    }    public void setlKontinent(String lKontinent) {        this.lKontinent = lKontinent;    }    public String getlIme() {        return lIme;    }    public void setlIme(String lIme) {        this.lIme = lIme;    }    public String getMestoNaOdrzuvanje() {        return mestoNaOdrzuvanje;    }    public void setMestoNaOdrzuvanje(String mestoNaOdrzuvanje) {        this.mestoNaOdrzuvanje = mestoNaOdrzuvanje;    }}