package com.EsportLeagueStats.ESLS.web.requests;public class AddLigaRequest {    private String kontinent;    private String ime;    private String mestoNaOdrzuvanje;    public AddLigaRequest(){}    public AddLigaRequest(String kontinent, String ime, String mestoNaOdrzuvanje) {        this.kontinent = kontinent;        this.ime = ime;        this.mestoNaOdrzuvanje = mestoNaOdrzuvanje;    }    public String getKontinent() {        return kontinent;    }    public String getIme() {        return ime;    }    public String getMestoNaOdrzuvanje() {        return mestoNaOdrzuvanje;    }}