package com.EsportLeagueStats.ESLS.web.requests;import org.springframework.format.annotation.DateTimeFormat;import java.time.LocalDate;public class AddSezonaRequest {    private int year;    @DateTimeFormat(iso = DateTimeFormat.ISO.DATE)    private LocalDate pocetenDatum;    @DateTimeFormat(iso = DateTimeFormat.ISO.DATE)    private LocalDate kraenDatum;    public AddSezonaRequest() {    }    public AddSezonaRequest(int year,                            LocalDate pocetenDatum,                            LocalDate kraenDatum) {        this.year = year;        this.pocetenDatum = pocetenDatum;        this.kraenDatum = kraenDatum;    }    public LocalDate getPocetenDatum() {        return pocetenDatum;    }    public void setPocetenDatum(LocalDate pocetenDatum) {        this.pocetenDatum = pocetenDatum;    }    public LocalDate getKraenDatum() {        return kraenDatum;    }    public void setKraenDatum(LocalDate kraenDatum) {        this.kraenDatum = kraenDatum;    }    public int getYear() {        return year;    }    public void setYear(int year) {        this.year = year;    }}