package com.EsportLeagueStats.ESLS.web.responses;import com.EsportLeagueStats.ESLS.model.Igrachi;import com.EsportLeagueStats.ESLS.model.Tim;import java.util.List;public class IgrachResponse {    private Integer iid;    private String akronim;    private String drzava;    private String ime;    private String pozicija;    private List<Tim> tims;    public IgrachResponse(Integer iid, String akronim, String drzava, String ime, String pozicija, List<Tim> tims) {        this.iid = iid;        this.akronim = akronim;        this.drzava = drzava;        this.ime = ime;        this.pozicija = pozicija;        this.tims = tims;    }    public static IgrachResponse of(Igrachi igrachi) {        return new IgrachResponse(igrachi.getIid(), igrachi.getAkronim(), igrachi.getDrzava(), igrachi.getIme(), igrachi.getPozicija(), igrachi.getTims());    }}